function toggleDarkMode(){let e=document.querySelector(".container");e.classList.toggle("dark-mode");let t=document.querySelector(".body");t.classList.toggle("dark-mode");let r=document.querySelector(".breadcrumbs");r.classList.toggle("dark-mode");let d=t.classList.contains("dark-mode");localStorage.setItem("darkModePreference",d)}window.addEventListener("load",function(){let e=localStorage.getItem("darkModePreference");if("true"===e){let t=document.querySelector(".container"),r=document.querySelector(".body"),d=document.querySelector(".breadcrumbs");t.classList.add("dark-mode"),r.classList.add("dark-mode"),d.classList.add("dark-mode")}});