function changeLanguage(e){let t=document.querySelectorAll(".disclaimer-content p");t.forEach(e=>{e.style.display="none"});let l=document.querySelectorAll(`.disclaimer-content .language-${e}`);l.forEach(e=>{e.style.display="block"});let a=document.querySelectorAll(".language-link");a.forEach(e=>{e.classList.remove("active")});let r=document.querySelector(`.language-link[data-lang="${e}"]`);r.classList.add("active")}function toggleDarkMode(){let e=document.querySelector(".container");e.classList.toggle("dark-mode");let t=document.querySelector(".body");t.classList.toggle("dark-mode");let l=document.querySelector(".breadcrumbs");l.classList.toggle("dark-mode");let a=t.classList.contains("dark-mode");localStorage.setItem("darkModePreference",a)}window.addEventListener("load",function(){let e=localStorage.getItem("darkModePreference");if("true"===e){let t=document.querySelector(".container"),l=document.querySelector(".body"),a=document.querySelector(".breadcrumbs");t.classList.add("dark-mode"),l.classList.add("dark-mode"),a.classList.add("dark-mode")}});